INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES
(1, 1, '2017-05-13 22:05:45', '2017-05-13 21:05:45', 'Welcome to WordPress. This is your first post. Edit or delete it, then start writing!', 'Hello world!', '', 'publish', 'open', 'open', '', 'hello-world', '', '', '2017-05-13 22:05:45', '2017-05-13 21:05:45', '', 0, 'http://localhost/wordpress/?p=1', 0, 'post', '', 1),
(2, 1, '2017-05-13 22:05:45', '2017-05-13 21:05:45', '<p style="text-align:justify;text-indent:25px;">Dans le cadre de la maintenance et d\'améliorations d’une application web en J2EE j’ai développé des composants d’applications telles que :</p>\r\n\r\n<ul style="text-align:justify;margin-left:20px;">\r\n	<li>des composants Web : des interfaces avec l\'utilisateur (JSP) réalisé avec les technologies JSTL, HTML, JavaScript, jQuery ;</li>\r\n	<li>des composants métiers : des classes Java (EJB) chargés du traitement métier des données et de la persistance des données par moyen d’Hibernate ou du JDBC.</li>\r\n</ul>\r\nTags:\r\nStruts, J2EE, JAVA, EL, JSTL, EJB, servlets, jsp, Hibernate, HTML, javascript, jQuery\r\n\r\nCette compétence est utilisé dans :\r\n<a href="/wordpress/application-web-j2ee/">Application WEB en J2EE</a>', 'Développement WEB en J2EE', '', 'publish', 'closed', 'open', '', 'competences-tech-j2ee', '', '', '2017-09-13 22:43:03', '2017-09-13 20:43:03', '', 0, 'http://localhost/wordpress/?page_id=2', 0, 'page', '', 0),
(199, 1, '2017-09-13 22:43:03', '2017-09-13 20:43:03', '<p style="text-align:justify;text-indent:25px;">Dans le cadre de la maintenance et d\'améliorations d’une application web en J2EE j’ai développé des composants d’applications telles que :</p>\r\n\r\n<ul style="text-align:justify;margin-left:20px;">\r\n	<li>des composants Web : des interfaces avec l\'utilisateur (JSP) réalisé avec les technologies JSTL, HTML, JavaScript, jQuery ;</li>\r\n	<li>des composants métiers : des classes Java (EJB) chargés du traitement métier des données et de la persistance des données par moyen d’Hibernate ou du JDBC.</li>\r\n</ul>\r\nTags:\r\nStruts, J2EE, JAVA, EL, JSTL, EJB, servlets, jsp, Hibernate, HTML, javascript, jQuery\r\n\r\nCette compétence est utilisé dans :\r\n<a href="/wordpress/application-web-j2ee/">Application WEB en J2EE</a>', 'Développement WEB en J2EE', '', 'inherit', 'closed', 'closed', '', '2-revision-v1', '', '', '2017-09-13 22:43:03', '2017-09-13 20:43:03', '', 2, '/2017/09/13/2-revision-v1/', 0, 'revision', '', 0),
(39, 1, '2017-05-17 13:42:16', '2017-05-17 12:42:16', '<p style="text-align: justify; text-indent: 25px;">Dans le cadre de la maintenance et d\'améliorations d’une application web en J2EE j’ai développé des composants d’applications telles que :</p>\r\n\r\n<ul style="text-align: justify; margin-left: 20px;">\r\n 	<li>des composants Web : des interfaces avec l\'utilisateur (JSP) réalisé avec les technologies JSTL, HTML, JavaScript, jQuery ;</li>\r\n 	<li>des composants métiers : des classes Java (EJB) chargés du traitement métier des données et de la persistance des données par moyen d’Hibernate ou du JDBC.</li>\r\n</ul>\r\nTags:\r\nStruts, J2EE, JAVA, EL, JSTL, EJB, servlets, jsp, Hibernate, HTML, javascript, jQuery\r\n\r\nCette compétence est utilisé dans :\r\n<a href="https://lioubouchkin.wordpress.com/projets-application-web/">Application WEB en J2EE</a>', 'Développement WEB en J2EE', '', 'inherit', 'closed', 'closed', '', '2-revision-v1', '', '', '2017-05-17 13:42:16', '2017-05-17 12:42:16', '', 2, 'http://localhost/wordpress/2017/05/17/2-revision-v1/', 0, 'revision', '', 0),
